import React from 'react';
import Menu from './modules/Menu'
import Mymap from './modules/Mymap'

class App extends React.Component {

  constructor(props) {
    super()
    this.state = {
        allNodes: require('./json/WALC.json'),
        floor: 3,
        findRoomId: '',
        marker: {position: [0, 0], floor: 0},
        mapBounds: [[0, 600], [450, 0]], 
        imageUrl: require("./img/firstFloor.jpg")
    }

    this.handleChangeFinder = this.handleChangeFinder.bind(this)
  }

  handleChangeFinder(event) {
    this.setState({findRoomId: event.target.value})
  }

  handleSubmitFinder(event) {
    this.setState({
      marker: {
        position: [this.state.allNodes[this.state.findRoomId].lat, this.state.allNodes[this.state.findRoomId].lon],
        floor: this.state.allNodes[this.state.findRoomId].floor
      }
    })
    event.preventDefault()
  }

  render() {
    return (
      <div className="App">
        <Menu 
          findRoomId={this.state.findRoomId}
          handleChangeFinder={this.handleChangeFinder}
          handleSubmitFinder={this.handleSubmitFinder}
        />
        <Mymap 
          marker={this.state.marker}
          mapBounds={this.state.mapBounds} 
          imageUrl={this.state.imageUrl} 
          imageBounds={[[this.state.mapBounds[1][0],0], [0, this.state.mapBounds[0][1]]]}
        />
      </div>
    );
  }
}

export default App;
