import React from 'react';
import Menu from './modules/Menu'
import Mymap from './modules/Mymap'

class App extends React.Component {

  constructor(props) {
    super(props)
    this.state = {
        mode: 'finder',
        building: 'WALC',
        allNodes: (require('./json/WALC.json'))[0],
        floor: 0,
        findRoomId: '',
        sourceRoomId: '',
        destinationRoomId: '',
        marker: {position: [], floor: 0},
        mapBounds: [[0, 600], [450, 0]]
    }
    this.handleChangeFinder = this.handleChangeFinder.bind(this)
    this.handleSubmitFinder = this.handleSubmitFinder.bind(this)
    this.validateId = this.validateId.bind(this)
    this.handleChangeRouterSource = this.handleChangeRouterSource.bind(this)
    this.handleChangeRouterDestination = this.handleChangeRouterDestination.bind(this)
    this.handleSubmitRouter = this.handleSubmitRouter.bind(this)
  }

  validateId(id) {
    return (typeof id === 'string' && id in this.state.allNodes)
  }

  handleChangeFinder(event) {
    this.setState({findRoomId: event.target.value.split('').map(x => typeof x == 'string' ? x.toUpperCase() : x).join('')})
  }

  handleSubmitFinder(event) {
    if(!this.validateId(this.state.findRoomId))
      alert("Please input valid room number")
    else{
      this.setState({
        marker: {
          position: [this.state.allNodes[this.state.findRoomId].lat, this.state.allNodes[this.state.findRoomId].lon],
          floor: this.state.allNodes[this.state.findRoomId].floor
        },
        floor: this.state.allNodes[this.state.findRoomId].floor
      })
    }
    event.preventDefault()
  }

  handleChangeRouterSource(event) {
    this.setState({sourceRoomId: event.target.value.split('').map(x => typeof x == 'string' ? x.toUpperCase() : x).join('')})
  }

  handleChangeRouterDestination(event) {
    this.setState({destinationRoomId: event.target.value.split('').map(x => typeof x == 'string' ? x.toUpperCase() : x).join('')})
  }

  handleSubmitRouter() {

  }

  render() {
    return (
      <div className="App">
        <Menu 
          findRoomId={this.state.findRoomId}
          handleChangeFinder={this.handleChangeFinder}
          handleSubmitFinder={this.handleSubmitFinder}
          sourceRoomId={this.sourceRoomId}
          handleChangeRouterSource={this.handleChangeRouterSource}
          destinationRoomId={this.destinationRoomId}
          handleChangeRouterDestination={this.handleChangeRouterDestination}
          handleSubmitRouter={this.handleSubmitRouter}
        />
        <Mymap 
          marker={this.state.marker.floor === this.state.floor ? this.state.marker : {position: [], floor: this.state.marker.floor}}
          mapBounds={this.state.mapBounds} 
          imageUrl={require('./img/' + this.state.building + '/' + this.state.floor + '.jpg')} 
          imageBounds={[[this.state.mapBounds[1][0],0], [0, this.state.mapBounds[0][1]]]}
        />
      </div>
    );
  }
}

export default App;
