import React from 'react';
import { Map, Marker, Popup, ImageOverlay} from 'react-leaflet'
import '../styles/myMap.css';
import '../styles/leaflet.css'
import icon from '../img/marker-icon.png';
import iconShadow from '../img/marker-shadow.png';
import L from 'leaflet'

class Mymap extends React.Component {

    constructor() {
        super()
        this.state = {
            position: [0, 0],
            zoom: 16
          }
        const DefaultIcon = L.icon({
            iconUrl: icon,
            shadowUrl: iconShadow,
            iconSize: [25, 41],
            iconAnchor: [12.5, 41],
            popupAnchor: [0, -41]
        });
        L.Marker.prototype.options.icon = DefaultIcon;
    }

    render() {
        return (
            <Map crs={L.CRS.Simple} bounds = {[[0, 600], [450, 0]]} center={this.state.position} zoom={this.state.zoom}>
                <ImageOverlay url = {require("../img/firstFloor.jpg")} bounds = {[[450, 0], [0, 600]]}
                />
                <Marker position={this.state.position}>
                    <Popup>A pretty CSS3 popup.<br />Easily customizable.</Popup>
                </Marker>
          </Map>)
    }
}

export default Mymap
